        <script>
            //UTM parameters to GET from the URL as Pantheon does not support from the PHP
            //https://github.com/westonruter/pantheon-documentation/blob/master/source/docs/articles/architecture/edge/pantheon_stripped-get-parameter-values.md
            function setCookie(cname, cvalue, exdays) {
                var d = new Date();
                d.setTime(d.getTime() + (exdays * 24 * 60 * 60 * 1000));
                var expires = "expires="+d.toUTCString();
                document.cookie = cname + "=" + cvalue + ";" + expires + ";path=/";
            }
            function getQueryParams(qs) {
                qs = qs.split("+").join(" ");
                var params = {}, tokens, re = /[?&]?([^=]+)=([^&]*)/g;
                while (tokens = re.exec(qs)) {
                    params[decodeURIComponent(tokens[1])] = decodeURIComponent(tokens[2]);
                }
                return params;
            }
            var query_params = getQueryParams(document.location.search);
            //console.log(query_params);

            if (query_params['utm_campaign']) {
                setCookie('utm_campaign', query_params['utm_campaign'], 1);
            }
            if (query_params['utm_content']) {
                setCookie('utm_content', query_params['utm_content'], 1);
            }
            if (query_params['utm_medium']) {
                setCookie('utm_medium', query_params['utm_medium'], 1);
            }
            if (query_params['utm_source']) {
                setCookie('utm_source', query_params['utm_source'], 1);
            }
            if (query_params['utm_term']) {
                setCookie('utm_term', query_params['utm_term'], 1);
            }
            if (query_params['utm_id']) {
                setCookie('utm_id', query_params['utm_id'], 1);
            }
            if (query_params['url_tracking_code']) {
                setCookie('url_tracking_code', query_params['url_tracking_code'], 1);
            }
            //End of UTM Parameters
        </script>


    
